import tatiyeNet,{modalEntri,mainRouterId,modalRouteFrom,useHandelIDTabel}  from "../es6.js"; 
export function setDropdown(element,id) {
 
     var setdropdown='';
     $(element.lengthMenu).each(function(index,key) {
        $.each(key,function(keydwn,dwn){
              if (dwn[0]=='download') {
                var appfile =useHandelIDTabel('appfile,'+element.key);
                 setdropdown +=`<a  href="${tatiyeNet.hostName+'/'+appfile['filename']}" class="pt-5px dropdown-item"target="_blank">
                       <i class="icon-feather-${dwn[2]} fs-14px mr-5px"></i> ${keydwn}</a>`;
              } else {
                 setdropdown +=`<a  onclick="useClick(['${dwn[0]}','${element.key}','${dwn[1]}','${dwn[3]}','${dwn[4]}']);" href="javascript:void(0);" class="pt-5px dropdown-item ">
                 <i class="feat feat-${dwn[2]} fs-14px mr-5px"></i> ${keydwn}</a>`;
              }
        });
     });
     var desainTabel=`
        <a href="javascript:void(0);" class="dropdown-link" data-toggle="dropdown"><i class="feat feat-more-vertical"></i></a>
        <div class="dropdown-menu">
            ${setdropdown}
        </div>
       `;
     $(element.app).html(desainTabel);
};
