import tatiyeNet,{modalEntri,mainRouterId,itemSpinner}  from "../es6.js"; 
export function setfrom(row,storage) {
          var myFrom='/helpers/Form/myOutFrom';
          var actionFrom    =JSON.stringify(row.action);
          var actionStorage =JSON.stringify(storage);

            $.post(tatiyeNet.etc,{
                 'etc'          :myFrom,
                 'action'       :actionFrom,
                 'storage'      :actionStorage,
                 beforeSend: function() {
                  $("#body"+row.autoload).html(itemSpinner('modal'));    
                 },
                })
               .done(function (template) {
                 $("#"+row.autoload).html('<form id="form_'+row.autoload+'" method="POST" enctype="multipart/form-data"> <div class="row row-xs">'+template+'</div> </form>');
                });
};

