import tatiyeNet,{modalEntri,mainRouterId,itemSpinner}  from "../es6.js"; 
export function share(row) {
   var desain=`<div class="shareLink"id="appshareLink">
   <ul>
      <li><span>Share</span></li>
      <li>
         <a  id="fb"href="javascript:void(0);">
            <i class="fab fa-facebook"></i> 
         </a>
      </li>
      <li>
         <a  id="twitter"href="javascript:void(0);">
            <i style="color:#00B8D4"  class="fab fa-twitter"></i>
         </a>
      </li>
      <li>
         <a href="https://api.whatsapp.com/send?text=${row.baseLink}" data-action="share/whatsapp/share" target="_blank">
            <i style="color:#10B759" class="icon-brand-whatsapp-square"></i>
         </a>
      </li>
   </ul>
</div>`;   
$(row.atribut).html(desain);
const appDiv = document.getElementById('appshareLink');
const fb = document.getElementById('fb');
fb.addEventListener('click', shareOnFacebook);

const tweet = document.getElementById('twitter');
tweet.addEventListener('click', shareOnTwitter);

function shareOnFacebook() {
  const navUrl =
    'https://www.facebook.com/sharer/sharer.php?u='+row.baseLink;
  window.open(navUrl, '_blank');
}

function shareOnTwitter() {
  const navUrl =
    'https://twitter.com/intent/tweet?text='+row.baseLink;
  window.open(navUrl, '_blank');
}




};
