import tatiyeNet,{Firebase,setToken,Qrcode,userAgent,QRLogin}  from "../es6.js";
var agent=userAgent({'indexOn':'Desktop'})
  //console.log(agent.ipaddress)
  var newTokenQr=setToken({
    tm:agent.tim,
    domain:agent.ipaddress,
  });
var Apps="Ngorei_"+newTokenQr
let db=Firebase({"tabel":[Apps,'select']});
 db.on("value", function (select) {
     select.forEach(function(row){
        var post = row.val();
        if (post.id==Apps) {
            QRLogin(post,Apps)
        }
     });  
 });
 Qrcode({
      element:"qrious",
      size: 250,
      background:'#fff',
      foreground:'#000',
      value: Apps
 })