import tatiyeNet from "../es6.js"; 
export function spinner(load,top='200px') { 
let style=`
    <div style="width:200px;margin: auto; margin-top:${top};" class="text-center">
      <div class="spinner-border ${load} " role="status">
       <span class="sr-only"></span>
      </div>
    </div>`;
return style;
};
